import { mapFieldCoords } from './fieldMap';

export const POSITION_ZONES = {
  Goalkeeper: { y: 95, xStart: 25, xEnd: 65 },
  'Center Back': { y: 78, xStart: -10, xEnd: 100 },
  'Full Back': { y: 85, xStart: -80, xEnd: 140 },
  'Wing Back': { y: 68, xStart:-10, xEnd: 90 },
  'Defensive Midfielder': { y: 60, xStart: 24, xEnd: 68 },
  'Central Midfielder': { y: 50, xStart: -5, xEnd: 88 },
  'Attacking Midfielder': { y: 38, xStart: 20, xEnd: 73 },
  Winger: { y: 25, xStart: 5, xEnd: 90 },
  Striker: { y: 10, xStart: 40, xEnd: 60 }
};

export function generateDynamicPositionCoords(position, count) {
  const zone = POSITION_ZONES[position];
  if (!zone) return Array(count).fill(mapFieldCoords(50, 50)); // fallback

  const { y, xStart, xEnd } = zone;
  const spacing = (xEnd - xStart) / Math.max(count + 0.5, 2); // slightly more space

  return Array.from({ length: count }, (_, index) => {
    const x = xStart + spacing * (index + 1);
    return mapFieldCoords(x, y );
  });
}
