import React, { useState, useMemo, useCallback } from 'react';
import playersData from './playersData.json'; 
import { POSITION_COORDS_MAP, orderedPositionSlots } from './positionCoords';
import { mapFieldCoords } from './fieldMap';

const PLAYERS_DATA = playersData; // Import players data from playersData.js

const POSITION_GROUPS = {
  Forwards: ['Winger', 'Striker'],
  Midfielders: ['Defensive Midfielder', 'Central Midfielder', 'Attacking Midfielder'],
  Defenders: ['Center Back', 'Wing Back', 'Full Back'],
  Goalkeepers: ['Goalkeeper'],
};

function App() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [highlightedPlayersInGroup, setHighlightedPlayersInGroup] = useState([]);
  // State to manage which players are currently on the field
  const [onFieldPlayers, setOnFieldPlayers] = useState(() => PLAYERS_DATA.map(player => player.id));

  // Memoized list of players currently on the field with their assigned coordinates
const playersOnFieldWithCoords = useMemo(() => {
  const groupedPlayers = {};

  PLAYERS_DATA.filter(p => onFieldPlayers.includes(p.id)).forEach(player => {
    if (!groupedPlayers[player.position]) groupedPlayers[player.position] = [];
    groupedPlayers[player.position].push(player);
  });

  const playerWithCoords = [];

  for (const [position, players] of Object.entries(groupedPlayers)) {
    const coordsList = generateDynamicPositionCoords(position, players.length);

    players.forEach((player, index) => {
      const coords = coordsList[index] || mapFieldCoords(50, 50);
      playerWithCoords.push({ ...player, coords });
    });
  }

  return playerWithCoords;
}, [onFieldPlayers]);

  // Handles player clicks on the field or in the left list
  const handlePlayerClick = useCallback((player) => {
    setSelectedPlayer(prevSelectedPlayer =>
      prevSelectedPlayer && prevSelectedPlayer.id === player.id ? null : player
    );
  }, []);

  // Handles group button clicks
  const handleGroupClick = useCallback((groupName) => {
    const newSelectedGroup = groupName === selectedGroup ? null : groupName;
    setSelectedGroup(newSelectedGroup);
    setSelectedPlayer(null); // Clear selected player when group changes

    if (newSelectedGroup) {
      const playersForGroup = PLAYERS_DATA.filter(player => {
        const positionsInGroup = POSITION_GROUPS[newSelectedGroup];
        return positionsInGroup && positionsInGroup.includes(player.position);
      });
      setHighlightedPlayersInGroup(playersForGroup.map(p => p.id)); // Highlight these players in the left list
    } else {
      setHighlightedPlayersInGroup([]); // Clear highlighting
    }
  }, [selectedGroup]);

  // Determines if a player should be highlighted on the field (selected or in the currently selected group)
  const isPlayerHighlightedOnField = useCallback((player) => {
    // If a specific player is selected, only that player is highlighted
    if (selectedPlayer && selectedPlayer.id === player.id) {
      return true;
    }
    // If a group is selected, and this player is in that group, highlight them
    if (selectedGroup) {
      const positionsInGroup = POSITION_GROUPS[selectedGroup];
      return positionsInGroup && positionsInGroup.includes(player.position);
    }
    return false;
  }, [selectedPlayer, selectedGroup]);

  // Determines if a player should be visible on the field (if a group is selected, only show players from that group)
  const isPlayerVisibleOnField = useCallback((player) => {
    if (!selectedGroup) {
      return true; // All players visible if no group is selected
    }
    const positionsInGroup = POSITION_GROUPS[selectedGroup];
    return positionsInGroup && positionsInGroup.includes(player.position);
  }, [selectedGroup]);

  // Toggles a player's presence on the field
  const togglePlayerOnField = useCallback((playerId) => {
    setOnFieldPlayers(prevOnFieldPlayers => {
      if (prevOnFieldPlayers.includes(playerId)) {
        return prevOnFieldPlayers.filter(id => id !== playerId);
      } else {
        return [...prevOnFieldPlayers, playerId];
      }
    });
  }, []);

  // Placeholder for background image. Replace with your actual image path.
  // Example: import fieldBg from './field-bg.jpg';
  const BACKGROUND_IMAGE_URL = 'field-bg.jpg'; // Replace with your actual image path

  return (
    <div className="font-inter flex flex-col items-center min-h-screen bg-gray-900 text-white p-4 sm:p-6 md:p-8">
      <h1 className="text-3xl sm:text-4xl font-bold mb-6 text-center text-green-400">
        Football Team Roster
      </h1>

      {/* Group Filter Buttons */}
      <div className="w-full flex flex-wrap justify-center gap-2 mb-6 p-2 bg-gray-700/70 rounded-md max-w-7xl">
        {Object.keys(POSITION_GROUPS).map((group) => (
          <button
            key={group}
            onClick={() => handleGroupClick(group)}
            className={`px-4 py-2 rounded-full font-semibold text-sm sm:text-base transition-all duration-300
              ${selectedGroup === group ? 'bg-green-600 text-white shadow-lg scale-105' : 'bg-green-700 text-gray-200 hover:bg-green-500 hover:text-white'}`}
          >
            {group}
          </button>
        ))}
      </div>

      <div className="flex flex-col md:flex-row w-full max-w-7xl bg-gray-800 rounded-lg shadow-xl overflow-hidden">
        {/* Left Sidebar: All Players List */}
        <div className="w-full md:w-64 lg:w-80 p-6 bg-gray-900 rounded-l-lg md:rounded-r-none"> {/* Removed max-h-[800px] and overflow-y-auto */}
          <h2 className="text-2xl font-bold mb-6 text-green-400">All Players</h2>
          <ul className="flex flex-col gap-2">
            {PLAYERS_DATA.map(player => (
              <li
                key={player.id}
                className={`p-3 rounded-md cursor-pointer transition-colors duration-200 flex items-center justify-between
                  ${selectedPlayer && selectedPlayer.id === player.id ? 'bg-blue-600 text-white shadow-lg' :
                    highlightedPlayersInGroup.includes(player.id) ? 'bg-yellow-700 text-white' :
                    'bg-gray-700 hover:bg-gray-600'}`}
                onClick={() => handlePlayerClick(player)}
              >
                <div>
                  <span className="font-semibold text-lg">{player.name}</span>
                  <span className="text-sm text-gray-400 ml-2">#{player.jersey_number}</span>
                </div>
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={onFieldPlayers.includes(player.id)}
                    onChange={() => togglePlayerOnField(player.id)}
                    onClick={(e) => e.stopPropagation()} // Prevent player selection when clicking checkbox
                    className="form-checkbox h-5 w-5 text-green-500 rounded border-gray-300 focus:ring-green-500"
                  />
                  <span className="text-sm text-gray-300">On Field</span>
                </label>
              </li>
            ))}
          </ul>
        </div>

        {/* Center: Field Map */}
        <div className="relative w-full pb-[120%] sm:pb-[100%] md:pb-[90%]">
          <div className="relative w-full pb-[160%]"
            style={{ backgroundImage: `url(${BACKGROUND_IMAGE_URL})`, backgroundSize: 'contain', backgroundPosition: 'center', backgroundRepeat: 'no-repeat', backgroundColor: '#3b5f3b' }}>
            {playersOnFieldWithCoords.map((player) => {
              const isHighlighted = isPlayerHighlightedOnField(player);
              const isVisible = isPlayerVisibleOnField(player);

              if (!isVisible) return null; // Hide players not in the selected group

              return (
                <div
                  key={player.id}
                  title={player.name}
                  className={`absolute flex items-center justify-center w-12 h-12 rounded-full font-bold text-sm cursor-pointer transition-all duration-300
                    transform -translate-x-1/2 -translate-y-1/2 
                    ${isHighlighted ? 'bg-yellow-400 border-yellow-300 scale-110 text-black' : 'bg-white/80 hover:bg-green-300 text-black border-white'}
                  `}
                  style={{ top: player.coords.top, left: player.coords.left }}
                  onClick={() => handlePlayerClick(player)}
                >
                  #{player.jersey_number}
                </div>
              );
            })}
          </div>
        </div>

        {/* Right Sidebar: Player Details */}
        <div className="w-full md:w-80 lg:w-96 p-6 bg-gray-900 rounded-r-lg md:rounded-bl-none"> {/* Removed max-h-[800px] and overflow-y-auto */}
          <h2 className="text-2xl font-bold mb-6 text-green-400">Player Details</h2>
          {selectedPlayer && (
            <div className="bg-gray-800 p-6 rounded-lg shadow-md mb-4">
              <h3 className="text-xl font-bold text-white mb-2">{selectedPlayer.name}</h3>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Jersey Number:</strong> {selectedPlayer.jersey_number}
              </p>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Position:</strong> {selectedPlayer.position}
              </p>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Foot Preference:</strong> {selectedPlayer.foot}
              </p>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Goals:</strong> {selectedPlayer.goals}
              </p>
              <p className="text-gray-300 mb-4">
                <strong className="text-green-300">Assists:</strong> {selectedPlayer.assists}
              </p>

              <div className="mb-4">
                <p className="text-gray-300 mb-2">
                  <strong className="text-green-300">Fitness Level:</strong> {selectedPlayer.fitness_level}%
                </p>
                <div className="w-full bg-gray-600 rounded-full h-3">
                  <div
                    className="bg-green-500 h-3 rounded-full transition-all duration-500 ease-out"
                    style={{ width: `${selectedPlayer.fitness_level}%` }}
                  ></div>
                </div>
              </div>
            </div>
          )}

          {!selectedPlayer && (
            <p className="text-gray-400 text-center py-10">
              Click on a player on the field or from the 'All Players' list to view details.
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
