import React, { useState, useEffect, useMemo } from 'react';
import PLAYERS_DATA from './playerData.json'; // Import player data from JSON file

const POSITION_GROUPS = {
  Forwards: ['Winger', 'Striker'], // 'WF' or 'ST/CF'
  Midfielders: ['Defensive Midfielder', 'Central Midfielder', 'Attacking Midfielder'], // 'DM', 'CM', 'AM'
  Defenders: ['Center Back', 'Wing Back', 'Full Back'], // 'CB', 'WB', 'FB'
  Goalkeepers: ['Goalkeeper'], // 'GK'
};

const FIELD_AREA_LEFT_OFFSET = 12;
const FIELD_AREA_RIGHT_OFFSET = 12;
const FIELD_AREA_TOP_OFFSET = 12;
const FIELD_AREA_BOTTOM_OFFSET = 12;

const FIELD_EFFECTIVE_WIDTH_RATIO = (100 - FIELD_AREA_LEFT_OFFSET - FIELD_AREA_RIGHT_OFFSET) / 100;
const FIELD_EFFECTIVE_HEIGHT_RATIO = (100 - FIELD_AREA_TOP_OFFSET - FIELD_AREA_BOTTOM_OFFSET) / 100;

const mapFieldCoords = (originalLeft, originalTop) => {
    const adjustedLeft = FIELD_AREA_LEFT_OFFSET + (originalLeft * FIELD_EFFECTIVE_WIDTH_RATIO);
    const adjustedTop = FIELD_AREA_TOP_OFFSET + (originalTop * FIELD_EFFECTIVE_HEIGHT_RATIO);
    return { top: `${adjustedTop}%`, left: `${adjustedLeft}%`, transform: 'translateX(-50%)' };
};

const POSITION_COORDS_MAP = {
  'Goalkeeper_1': mapFieldCoords(38, 102), // John Smith 
  'Goalkeeper_2': mapFieldCoords(61, 102),  // Logan Thompson 
  'Full Back_L1': mapFieldCoords(5, 85),   // David Wilson (LB)
  'Center Back_1': mapFieldCoords(23, 81), // Michael Brown (LCB) 
  'Center Back_2': mapFieldCoords(78, 81), // Chris Lee (RCB) 
  'Full Back_R1': mapFieldCoords(96, 85),  // Mark Taylor (RB)
  'Center Back_3': mapFieldCoords(40, 78), // Lucas Garcia
  'Center Back_4': mapFieldCoords(63, 78), // Mason Martinez
  'Wing Back_L1': mapFieldCoords(10, 69), // Jayden Robinson (LWB)
  'Wing Back_R1': mapFieldCoords(95, 69), // Elijah Clark (RWB)
  'Defensive Midfielder_1': mapFieldCoords(35, 58), // Daniel Anderson (DM Left) 
  'Defensive Midfielder_2': mapFieldCoords(65, 58), // Sebastian Lewis (DM Right) 
  'Central Midfielder_1': mapFieldCoords(23, 48),  // James Thomas (LCM)
  'Central Midfielder_2': mapFieldCoords(78, 48),  // Jackson Young (RCM)
  'Attacking Midfielder_1': mapFieldCoords(35, 36), // Robert Jackson (AM Left) 
  'Attacking Midfielder_2': mapFieldCoords(65, 36), // Aiden King (AM Right)
  'Winger_L1': mapFieldCoords(10, 27), // William White (LW) 
  'Striker_1': mapFieldCoords(40, 12),  // Ethan Martin (CF Left)
  'Winger_R1': mapFieldCoords(90, 27), // Thomas Harris (RW) 
  'Winger_L2': mapFieldCoords(25, 20), // Nathan Hall (LW Reserve)
  'Winger_R2': mapFieldCoords(75, 20), // Oliver Adams (RW Reserve)
  'Striker_2': mapFieldCoords(60, 12), // Henry Scott (CF Right)
};

const orderedPositionSlots = {
    'Goalkeeper': ['Goalkeeper_1', 'Goalkeeper_2'],
    'Center Back': ['Center Back_1', 'Center Back_2', 'Center Back_3', 'Center Back_4'],
    'Full Back': ['Full Back_L1', 'Full Back_R1'],
    'Wing Back': ['Wing Back_L1', 'Wing Back_R1'],
    'Defensive Midfielder': ['Defensive Midfielder_1', 'Defensive Midfielder_2'],
    'Central Midfielder': ['Central Midfielder_1', 'Central Midfielder_2'],
    'Attacking Midfielder': ['Attacking Midfielder_1', 'Attacking Midfielder_2'],
    'Winger': ['Winger_L1', 'Winger_R1', 'Winger_L2', 'Winger_R2'],
    'Striker': ['Striker_1', 'Striker_2'],
};

function App() {
  const [selectedGroup, setSelectedGroup] = useState(null); // State for selected position group
  const [selectedPlayer, setSelectedPlayer] = useState(null); // State for selected individual player
  const [displayedPlayersInGroup, setDisplayedPlayersInGroup] = useState([]); // State for players list when group is selected
  const playersWithCoords = useMemo(() => {
    const positionCounters = {
      'Goalkeeper': 0,
      'Center Back': 0,
      'Full Back': 0,
      'Wing Back': 0,
      'Defensive Midfielder': 0,
      'Central Midfielder': 0,
      'Attacking Midfielder': 0,
      'Winger': 0,
      'Striker': 0,
    };

    const getAssignedCoords = (player) => {
      const positionType = player.position;
      let slotKey = null;

      let currentCount = positionCounters[positionType];
      let slots = orderedPositionSlots[positionType];

      if (!slots || slots.length === 0) {
        console.warn(`No defined slots for position type: ${positionType}`);
        return { top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }; // Fallback
      }

      slotKey = slots[currentCount % slots.length];

      positionCounters[positionType]++;

      return POSITION_COORDS_MAP[slotKey] || { top: '50%', left: '50%', transform: 'translate(-50%, -50%)' };
    };

    return PLAYERS_DATA.map(player => ({
      ...player,
      coords: getAssignedCoords(player)
    }));
  }, [PLAYERS_DATA]);

  const handlePlayerClick = (player) => {
    setSelectedPlayer(prevSelectedPlayer => 
      prevSelectedPlayer && prevSelectedPlayer.id === player.id ? null : player
    );
  };

  const handleGroupClick = (groupName) => {
    const newSelectedGroup = groupName === selectedGroup ? null : groupName;
    setSelectedGroup(newSelectedGroup);
    setSelectedPlayer(null); 

    if (newSelectedGroup) {
      const playersForGroup = playersWithCoords.filter(player => {
        const positionsInGroup = POSITION_GROUPS[newSelectedGroup];
        return positionsInGroup && positionsInGroup.includes(player.position);
      });
      setDisplayedPlayersInGroup(playersForGroup);
    } else {
      setDisplayedPlayersInGroup([]); // Clear the list if no group is selected
    }
  };

  // Determine if a player should be highlighted based on selected group or individual player
  const isPlayerHighlighted = (player) => {
    // Highlight if this is the selected individual player
    if (selectedPlayer && selectedPlayer.id === player.id) {
      return true; 
    }
    // Also highlight if a group is selected AND the player belongs to that group
    if (selectedGroup) {
      const positionsInGroup = POSITION_GROUPS[selectedGroup];
      return positionsInGroup && positionsInGroup.includes(player.position);
    }
    return false; // No highlight by default
  };

  // Football field background image URL
  const BACKGROUND_IMAGE_URL = '/field-bg.jpg';


  return (
    // Tailwind CSS CDN for styling - ensure this is in your public/index.html or equivalent
    // <script src="https://cdn.tailwindcss.com"></script>
    <div className="font-inter flex flex-col items-center min-h-screen bg-gray-900 text-white p-4 sm:p-6 md:p-8">
      <h1 className="text-3xl sm:text-4xl font-bold mb-6 text-center text-green-400">
        Football Team Roster
      </h1>

      {/* Position Group Buttons - Moved to the top, after the main title */}
      <div className="w-full flex flex-wrap justify-center gap-2 mb-6 p-2 bg-gray-700/70 rounded-md max-w-7xl">
        {Object.keys(POSITION_GROUPS).map((group) => (
          <button
            key={group}
            onClick={() => handleGroupClick(group)}
            className={`px-4 py-2 rounded-full font-semibold text-sm sm:text-base transition-all duration-300
              ${selectedGroup === group ? 'bg-green-600 text-white shadow-lg scale-105' : 'bg-green-700 text-gray-200 hover:bg-green-500 hover:text-white'}`}
          >
            {group}
          </button>
        ))}
      </div>

      {/* Main container for field and details */}
      <div className="flex flex-col md:flex-row w-full max-w-7xl bg-gray-800 rounded-lg shadow-xl overflow-hidden"> {/* Using max-w-7xl for the combined container */}
        {/* Football Field Section */}
        <div className="relative flex-1 bg-gray-900 rounded-l-lg overflow-hidden">
          {/* Inner div for responsive aspect ratio and background image */}
          <div className="relative w-full pb-[160%]" // Increased pb to make field significantly taller
               style={{ backgroundImage: `url(${BACKGROUND_IMAGE_URL})`, backgroundSize: 'contain', backgroundPosition: 'center', backgroundRepeat: 'no-repeat', backgroundColor: '#3b5f3b' }}>

            {/* Player Placements on Field */}
            {playersWithCoords.map((player) => {
              const isHighlighted = isPlayerHighlighted(player);

              return (
                <div
                  key={player.id}
                  className={`absolute flex flex-col items-center cursor-pointer transition-all duration-300 transform -translate-x-1/2
                    ${isHighlighted ? 'bg-yellow-400 text-gray-900 font-bold p-2 rounded-md shadow-2xl z-10 scale-110 ring-4 ring-yellow-300' : 'text-gray-900 hover:scale-105 hover:bg-white/70'}`}
                  style={{ top: player.coords.top, left: player.coords.left, transform: player.coords.transform }}
                  onClick={() => handlePlayerClick(player)}
                >
                  <span className={`text-sm sm:text-base md:text-lg whitespace-nowrap text-center ${isHighlighted ? 'text-gray-900' : 'text-white'}`}>
                    {player.name}
                  </span>
                  <span className={`text-xs ${isHighlighted ? 'text-gray-700' : 'text-gray-300'}`}>
                    #{player.jersey_number}
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Player Details Section - Now on the right side */}
        <div className="w-full md:w-80 lg:w-96 p-6 bg-gray-900 rounded-r-lg md:rounded-bl-none overflow-y-auto max-h-[800px]">
          <h2 className="text-2xl font-bold mb-6 text-green-400">Player Details</h2>

          {/* Conditional rendering for selected player details */}
          {selectedPlayer && (
            <div className="bg-gray-800 p-6 rounded-lg shadow-md mb-4"> {/* Added mb-4 for spacing */}
              <h3 className="text-xl font-bold text-white mb-2">{selectedPlayer.name}</h3>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Jersey Number:</strong> {selectedPlayer.jersey_number}
              </p>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Position:</strong> {selectedPlayer.position}
              </p>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Foot Preference:</strong> {selectedPlayer.foot}
              </p>
              <p className="text-gray-300 mb-1">
                <strong className="text-green-300">Goals:</strong> {selectedPlayer.goals}
              </p>
              <p className="text-gray-300 mb-4">
                <strong className="text-green-300">Assists:</strong> {selectedPlayer.assists}
              </p>

              {/* Fitness Level Progress Bar */}
              <div className="mb-4">
                <p className="text-gray-300 mb-2">
                  <strong className="text-green-300">Fitness Level:</strong> {selectedPlayer.fitness_level}%
                </p>
                <div className="w-full bg-gray-600 rounded-full h-3">
                  <div
                    className="bg-green-500 h-3 rounded-full transition-all duration-500 ease-out"
                    style={{ width: `${selectedPlayer.fitness_level}%` }}
                  ></div>
                </div>
              </div>
            </div>
          )}

          {/* Conditional rendering for group players list */}
          {displayedPlayersInGroup.length > 0 && (
            <div className="bg-gray-800 p-4 rounded-lg shadow-md">
                <h3 className="text-xl font-bold text-white mb-4">Players in {selectedGroup}</h3>
                <ul className="flex flex-col gap-2"> 
                    {displayedPlayersInGroup.map(player => (
                        <li 
                            key={player.id} 
                            onClick={() => handlePlayerClick(player)} // This now toggles the selectedPlayer
                            className={`p-3 rounded-md cursor-pointer transition-colors duration-200 
                                ${selectedPlayer && selectedPlayer.id === player.id ? 'bg-green-600 text-white shadow-lg' : 'bg-gray-700 hover:bg-gray-600'}`}
                        >
                            <span className="font-semibold text-lg">{player.name}</span>
                            <span className="text-sm text-gray-400 ml-2">#{player.jersey_number}</span>
                        </li>
                    ))}
                </ul>
            </div>
          )}

          {/* Default message if nothing is selected */}
          {!selectedPlayer && displayedPlayersInGroup.length === 0 && (
            <p className="text-gray-400 text-center py-10">
              Click on a player on the field or a position group button to view details.
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
