// App.js
import React, { useState, useMemo, useCallback } from 'react';
import playersData from './playersData.json';
import { mapFieldCoords } from './fieldMap';

const PLAYERS_DATA = playersData;

const POSITION_GROUPS = {
  Forwards: ['Winger', 'Striker'],
  Midfielders: ['Defensive Midfielder', 'Central Midfielder', 'Attacking Midfielder'],
  Defenders: ['Center Back', 'Wing Back', 'Full Back'],
  Goalkeepers: ['Goalkeeper']
};

const FIELD_Y_POSITIONS = {
  Forwards: 15,
  Midfielders: 35,
  Defenders: 55,
  Goalkeepers: 80
};

const BACKGROUND_IMAGE_URL = 'e0e61e18-858b-442b-8950-efa99a9f8b2a.png'; // flipped in CSS

export default function App() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [onFieldPlayers, setOnFieldPlayers] = useState(() => PLAYERS_DATA.map(p => p.id));

  const groupedCoords = useMemo(() => {
    const coordsMap = {};

    for (const [group, positions] of Object.entries(POSITION_GROUPS)) {
      const playersInGroup = PLAYERS_DATA.filter(
        p => onFieldPlayers.includes(p.id) && positions.includes(p.position)
      );

      const y = FIELD_Y_POSITIONS[group];
      const spacing = 100 / (playersInGroup.length + 1);

      playersInGroup.forEach((player, index) => {
        const x = spacing * (index + 1);
        coordsMap[player.id] = mapFieldCoords(x, y);
      });
    }

    return coordsMap;
  }, [onFieldPlayers]);

  const playersWithCoords = useMemo(() => {
    return PLAYERS_DATA.filter(p => onFieldPlayers.includes(p.id)).map(player => ({
      ...player,
      coords: groupedCoords[player.id] || mapFieldCoords(50, 50)
    }));
  }, [groupedCoords, onFieldPlayers]);

  const handlePlayerClick = useCallback(player => {
    setSelectedPlayer(prev => (prev?.id === player.id ? null : player));
  }, []);

  const togglePlayerOnField = useCallback(id => {
    setOnFieldPlayers(prev =>
      prev.includes(id) ? prev.filter(pid => pid !== id) : [...prev, id]
    );
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-white font-sans p-4">
      <h1 className="text-3xl font-bold text-center text-green-400 mb-6">Football Team Roster</h1>

      <div className="flex flex-col md:flex-row gap-6 max-w-7xl mx-auto">
        {/* Players Sidebar */}
        <div className="w-full md:w-64 bg-gray-800 p-4 rounded-lg space-y-4">
          <h2 className="text-xl font-bold text-green-300">All Players</h2>
          <ul className="space-y-2 max-h-[80vh] overflow-y-auto">
            {PLAYERS_DATA.map(player => (
              <li
                key={player.id}
                className={`p-2 rounded flex items-center justify-between cursor-pointer ${
                  onFieldPlayers.includes(player.id) ? 'bg-gray-700' : 'bg-gray-600'
                }`}
                onClick={() => handlePlayerClick(player)}
              >
                <span>#{player.jersey_number} {player.name}</span>
                <input
                  type="checkbox"
                  checked={onFieldPlayers.includes(player.id)}
                  onChange={() => togglePlayerOnField(player.id)}
                  onClick={e => e.stopPropagation()}
                />
              </li>
            ))}
          </ul>
        </div>

        {/* Field Area */}
        <div className="relative flex-1 aspect-[3/4] bg-green-800 rounded-lg overflow-hidden shadow-lg">
          <div
            className="absolute inset-0 bg-center bg-no-repeat bg-contain"
            style={{
              backgroundImage: `url(${BACKGROUND_IMAGE_URL})`,
              transform: 'scaleY(-1)' // Flip the image vertically
            }}
          />

          {playersWithCoords.map(player => (
            <div
              key={player.id}
              title={player.name}
              className={`absolute w-12 h-12 rounded-full flex items-center justify-center font-bold text-black cursor-pointer
                transform -translate-x-1/2 -translate-y-1/2
                ${selectedPlayer?.id === player.id ? 'bg-yellow-400 scale-110' : 'bg-white/90'}`}
              style={{
                top: `${player.coords.top}%`,
                left: `${player.coords.left}%`
              }}
              onClick={() => handlePlayerClick(player)}
            >
              #{player.jersey_number}
            </div>
          ))}
        </div>

        {/* Player Details */}
        <div className="w-full md:w-80 bg-gray-800 p-4 rounded-lg">
          <h2 className="text-xl font-bold text-green-300 mb-4">Player Details</h2>
          {selectedPlayer ? (
            <div>
              <p><strong>Name:</strong> {selectedPlayer.name}</p>
              <p><strong>Jersey #:</strong> {selectedPlayer.jersey_number}</p>
              <p><strong>Position:</strong> {selectedPlayer.position}</p>
              <p><strong>Foot:</strong> {selectedPlayer.foot}</p>
              <p><strong>Goals:</strong> {selectedPlayer.goals}</p>
              <p><strong>Assists:</strong> {selectedPlayer.assists}</p>
              <p><strong>Fitness:</strong> {selectedPlayer.fitness_level}%</p>
              <div className="bg-gray-600 h-3 rounded-full mt-2">
                <div
                  className="bg-green-500 h-3 rounded-full"
                  style={{ width: `${selectedPlayer.fitness_level}%` }}
                />
              </div>
            </div>
          ) : (
            <p className="text-gray-400">Select a player to view details.</p>
          )}
        </div>
      </div>
    </div>
  );
}
