// Define logical groups of positions
const POSITION_GROUPS = {
    Forwards: ['Winger', 'Striker', 'Attacking Midfielder'],
    Midfielders: ['Defensive Midfielder', 'Central Midfielder'],
    Defenders: ['Center Back', 'Wing Back', 'Full Back'],
    Goalkeepers: ['Goalkeeper'],
};

// Define the vertical starting position for each group on the field (as a percentage from the top of the canvas)
// These values are adjusted for the FULL field image (field-bg.jpg), assuming goal at top and bottom.
// We'll position the players predominantly in one half.
const groupTopOffsets = {
    Forwards: 25,     // Attacking third, closer to the opponent's goal (top)
    Midfielders: 50,  // Around the center line
    Defenders: 75,    // Defensive third, closer to your own goal
    Goalkeepers: 90,   // Near your own goal (bottom)
};

// Player pin radius as a percentage of the shorter canvas dimension
const PLAYER_RADIUS_PERCENT = 2.5;
// Font size when player pin is expanded (as a percentage of canvas height)
const EXPANDED_PLAYER_FONT_SIZE_PERCENT = 1.5;

function App() {
    const [selectedGroup, setSelectedGroup] = useState(null);
    const [selectedPlayer, setSelectedPlayer] = useState(null);
    const [onFieldPlayers, setOnFieldPlayers] = useState(() => PLAYERS_DATA.map(player => player.id));

    const canvasRef = useRef(null);
    const backgroundRef = useRef(new Image());
    const [backgroundImageLoaded, setBackgroundImageLoaded] = useState(false);

    useEffect(() => {
        backgroundRef.current.src = 'field-bg.jpg'; // Updated path to the full field image
        backgroundRef.current.onload = () => {
            setBackgroundImageLoaded(true);
        };
        backgroundRef.current.onerror = () => {
            console.error("Failed to load background image. Please check the path.");
            setBackgroundImageLoaded(false);
        };
    }, []);

    // Handles player clicks (both from canvas and from the left list)
    const handlePlayerClick = useCallback((player) => {
        setSelectedPlayer(prevSelectedPlayer =>
            prevSelectedPlayer && player && prevSelectedPlayer.id === player.id ? null : player
        );
    }, []);

    // Memoized list of players with their calculated pixel coordinates.
    const playersOnFieldWithCoords = useMemo(() => {
        const canvas = canvasRef.current;
        const fieldWidth = canvas ? canvas.width : 1;
        const fieldHeight = canvas ? canvas.height : 1;

        const playerRadius = Math.min(fieldWidth, fieldHeight) * (PLAYER_RADIUS_PERCENT / 100);

        let relevantPlayers = PLAYERS_DATA.filter(p => onFieldPlayers.includes(p.id));

        if (selectedGroup) {
            const positionsInGroup = POSITION_GROUPS[selectedGroup];
            relevantPlayers = relevantPlayers.filter(player =>
                positionsInGroup && positionsInGroup.includes(player.position)
            );
        }

        const playersByGroup = {};
        Object.keys(POSITION_GROUPS).forEach(groupName => {
            playersByGroup[groupName] = [];
            POSITION_GROUPS[groupName].forEach(position => {
                relevantPlayers.filter(p => p.position === position).forEach(player => {
                    playersByGroup[groupName].push(player);
                });
            });
        });

        const playerWithCoords = [];

        for (const [groupName, players] of Object.entries(playersByGroup)) {
            const totalInGroup = players.length;
            if (totalInGroup === 0) continue;

            const topOffset = groupTopOffsets[groupName];
            const y = (topOffset / 100) * fieldHeight;

            const horizontalStart = 20; // Start 20% from left edge of canvas
            const horizontalEnd = 80;   // End 80% from left edge of canvas
            const effectiveWidth = horizontalEnd - horizontalStart;

            players.forEach((player, index) => {
                let x;
                if (totalInGroup === 1) {
                    x = ((horizontalStart + effectiveWidth / 2) / 100) * fieldWidth;
                } else {
                    x = ((horizontalStart + (index / (totalInGroup - 1)) * effectiveWidth) / 100) * fieldWidth;
                }
                playerWithCoords.push({ ...player, x, y, radius: playerRadius });
            });
        }
        return playerWithCoords;
    }, [onFieldPlayers, selectedGroup, backgroundImageLoaded]);

    const drawField = useCallback(() => {
        const canvas = canvasRef.current;
        if (!canvas || !backgroundImageLoaded) return;

        const ctx = canvas.getContext('2d');
        const parent = canvas.parentElement;

        const parentWidth = parent.clientWidth;
        const parentHeight = parent.clientHeight;

        // Corrected aspect ratio for field-bg.jpg (1024 / 768 = ~1.3333)
        const aspectRatio = 1024 / 768; // Use precise ratio
        let canvasWidth = parentWidth;
        let canvasHeight = parentWidth * aspectRatio;

        if (canvasHeight > parentHeight) {
            canvasHeight = parentHeight;
            canvasWidth = parentHeight / aspectRatio;
        }

        canvas.width = canvasWidth;
        canvas.height = canvasHeight;

        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // --- Removed Canvas context transformation for flipping ---
        // Draw the background image directly
        ctx.drawImage(backgroundRef.current, 0, 0, canvas.width, canvas.height);

        // Now draw players
        playersOnFieldWithCoords.forEach(player => {
            const { x, y, radius } = player;
            const isHighlighted = selectedPlayer && selectedPlayer.id === player.id;
            const shouldExpand = selectedGroup && Object.values(POSITION_GROUPS[selectedGroup]).some(pos => player.position === pos);

            ctx.beginPath();
            ctx.arc(x, y, radius, 0, Math.PI * 2, false);
            ctx.fillStyle = isHighlighted ? 'yellow' : (shouldExpand ? '#10B981' : 'rgba(255, 255, 255, 0.8)');
            ctx.fill();

            ctx.lineWidth = 2;
            ctx.strokeStyle = isHighlighted ? 'orange' : (shouldExpand ? '#059669' : 'rgba(0,0,0,0.5)');
            ctx.stroke();

            ctx.fillStyle = isHighlighted ? 'black' : (shouldExpand ? 'white' : 'black');
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';

            if (shouldExpand) {
                const text = `${player.name} #${player.jersey_number}`;
                ctx.font = `${canvas.height * (EXPANDED_PLAYER_FONT_SIZE_PERCENT / 100)}px sans-serif`;
                ctx.fillText(text, x, y);
            } else {
                ctx.font = `${radius * 0.7}px sans-serif`;
                ctx.fillText(`#${player.jersey_number}`, x, y);
            }
        });
    }, [playersOnFieldWithCoords, selectedPlayer, selectedGroup, backgroundImageLoaded]);

    useEffect(() => {
        drawField();
    }, [drawField]);

    const handleCanvasClick = useCallback((event) => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const rect = canvas.getBoundingClientRect();
        const scaleX = canvas.width / rect.width;
        const scaleY = canvas.height / rect.height;

        const clickX = (event.clientX - rect.left) * scaleX;
        const clickY = (event.clientY - rect.top) * scaleY;

        let clickedPlayer = null;
        for (const player of playersOnFieldWithCoords) {
            const distance = Math.sqrt(Math.pow(clickX - player.x, 2) + Math.pow(clickY - player.y, 2));
            if (distance < player.radius) {
                clickedPlayer = player;
                break;
            }
        }
        handlePlayerClick(clickedPlayer);
    }, [playersOnFieldWithCoords, handlePlayerClick]);

    const handleGroupClick = useCallback((groupName) => {
        const newSelectedGroup = groupName === selectedGroup ? null : groupName;
        setSelectedGroup(newSelectedGroup);

        if (newSelectedGroup) {
            const playersInNewGroup = PLAYERS_DATA.filter(player => {
                const positionsInGroup = POSITION_GROUPS[newSelectedGroup];
                return positionsInGroup && positionsInGroup.includes(player.position) && onFieldPlayers.includes(player.id);
            });
            setSelectedPlayer(playersInNewGroup.length > 0 ? playersInNewGroup[0] : null);
        } else {
            setSelectedPlayer(null);
        }
    }, [selectedGroup, onFieldPlayers]);

    const togglePlayerOnField = useCallback((playerId) => {
        setOnFieldPlayers(prevOnFieldPlayers => {
            const newState = prevOnFieldPlayers.includes(playerId)
                ? prevOnFieldPlayers.filter(id => id !== playerId)
                : [...prevOnFieldPlayers, playerId];

            if (selectedPlayer && selectedPlayer.id === playerId) {
                setSelectedPlayer(null);
            } else if (selectedGroup) {
                const currentGroupPlayers = PLAYERS_DATA.filter(p =>
                    POSITION_GROUPS[selectedGroup].includes(p.position) && newState.includes(p.id)
                );
                if (currentGroupPlayers.length > 0) {
                    setSelectedPlayer(currentGroupPlayers[0]);
                } else {
                    setSelectedPlayer(null);
                }
            }
            return newState;
        });
    }, [selectedPlayer, selectedGroup]);

    return (
        <div className="font-inter flex flex-col items-center min-h-screen bg-gray-900 text-white p-4 sm:p-6 md:p-8">
            <h1 className="text-3xl sm:text-4xl font-bold mb-6 text-center text-green-400">
                Football Team Roster
            </h1>

            {/* Group Filter Buttons */}
            <div className="w-full flex flex-wrap justify-center gap-2 mb-6 p-2 bg-gray-700/70 rounded-md max-w-7xl">
                {Object.keys(POSITION_GROUPS).map((group) => (
                    <button
                        key={group}
                        onClick={() => handleGroupClick(group)}
                        className={`px-4 py-2 rounded-full font-semibold text-sm sm:text-base transition-all duration-300
                            ${selectedGroup === group ? 'bg-green-600 text-white shadow-lg scale-105' : 'bg-green-700 text-gray-200 hover:bg-green-500 hover:text-white'}`}
                    >
                        {group}
                    </button>
                ))}
            </div>

            <div className="flex flex-col md:flex-row w-full max-w-7xl bg-gray-800 rounded-lg shadow-xl overflow-hidden">
                {/* Left Sidebar: All Players List */}
                <div className="w-full md:w-64 lg:w-80 p-6 bg-gray-900 rounded-l-lg md:rounded-r-none overflow-y-auto max-h-[800px]">
                    <h2 className="text-2xl font-bold mb-6 text-green-400">All Players</h2>
                    <ul className="flex flex-col gap-2">
                        {PLAYERS_DATA.map(player => (
                            <li
                                key={player.id}
                                className={`p-3 rounded-md cursor-pointer transition-colors duration-200 flex items-center justify-between
                                    ${selectedPlayer && selectedPlayer.id === player.id ? 'bg-blue-600 text-white shadow-lg' :
                                        selectedGroup && Object.values(POSITION_GROUPS[selectedGroup]).includes(player.position) ? 'bg-yellow-700 text-white' :
                                        'bg-gray-700 hover:bg-gray-600'}`}
                                onClick={() => handlePlayerClick(player)}
                            >
                                <div>
                                    <span className="font-semibold text-lg">{player.name}</span>
                                    <span className="text-sm text-gray-400 ml-2">#{player.jersey_number}</span>
                                </div>
                                <label className="flex items-center space-x-2 cursor-pointer">
                                    <input
                                        type="checkbox"
                                        checked={onFieldPlayers.includes(player.id)}
                                        onChange={() => togglePlayerOnField(player.id)}
                                        onClick={(e) => e.stopPropagation()}
                                        className="form-checkbox h-5 w-5 text-green-500 rounded border-gray-300 focus:ring-green-500"
                                    />
                                    <span className="text-sm text-gray-300">On Field</span>
                                </label>
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Center: Field Map with Canvas */}
                {/* The pb-[133.33%] (based on 1.3333 aspectRatio) on the parent div maintains the aspect ratio */}
                <div className="relative w-full pb-[133.33%] md:pb-[90%] overflow-hidden bg-[#3b5f3b] flex items-center justify-center">
                    <canvas
                        ref={canvasRef}
                        className="absolute top-0 left-0 w-full h-full"
                        onClick={handleCanvasClick}
                    />
                    {!backgroundImageLoaded && (
                        <div className="absolute inset-0 flex items-center justify-center text-gray-400 z-10 bg-gray-900/70">
                            Loading Field...
                        </div>
                    )}
                </div>

                {/* Right Sidebar: Player Details */}
                <div className="w-full md:w-80 lg:w-96 p-6 bg-gray-900 rounded-r-lg md:rounded-bl-none overflow-y-auto max-h-[800px]">
                    <h2 className="text-2xl font-bold mb-6 text-green-400">Player Details</h2>
                    {selectedPlayer && (
                        <div className="bg-gray-800 p-6 rounded-lg shadow-md mb-4">
                            <h3 className="text-xl font-bold text-white mb-2">{selectedPlayer.name}</h3>
                            <p className="text-gray-300 mb-1">
                                <strong className="text-green-300">Jersey Number:</strong> {selectedPlayer.jersey_number}
                            </p>
                            <p className="text-gray-300 mb-1">
                                <strong className="text-green-300">Position:</strong> {selectedPlayer.position}
                            </p>
                            <p className="text-gray-300 mb-1">
                                <strong className="text-green-300">Foot Preference:</strong> {selectedPlayer.foot}
                            </p>
                            <p className="text-gray-300 mb-1">
                                <strong className="text-green-300">Goals:</strong> {selectedPlayer.goals}
                            </p>
                            <p className="text-gray-300 mb-4">
                                <strong className="text-green-300">Assists:</strong> {selectedPlayer.assists}
                            </p>

                            <div className="mb-4">
                                <p className="text-gray-300 mb-2">
                                    <strong className="text-green-300">Fitness Level:</strong> {selectedPlayer.fitness_level}%
                                </p>
                                <div className="w-full bg-gray-600 rounded-full h-3">
                                    <div
                                        className="bg-green-500 h-3 rounded-full transition-all duration-500 ease-out"
                                        style={{ width: `${selectedPlayer.fitness_level}%` }}
                                    ></div>
                                </div>
                            </div>
                        </div>
                    )}

                    {!selectedPlayer && (
                        <p className="text-gray-400 text-center py-10">
                            Click on a player on the field or from the 'All Players' list to view details.
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default App;
