// SummaryContainer.js
import React from 'react';
import { POSITION_GROUPS } from './fieldMap';  // Adjust path based on where POSITION_GROUPS lives

const SummaryContainer = ({ players }) => {
  if (!players || players.length === 0) {
    return (
      <div className="w-full bg-gray-800 text-white p-4 mt-4 rounded-xl shadow-lg">
        <h2 className="text-xl font-bold mb-2">Team Summary</h2>
        <p>No players selected.</p>
      </div>
    );
  }

  const total = players.length;
  const goals = players.reduce((acc, p) => acc + p.goals, 0);
  const assists = players.reduce((acc, p) => acc + p.assists, 0);
  const avgFitness = (players.reduce((acc, p) => acc + p.fitness_level, 0) / total).toFixed(1);

  const leftFooted = players.filter(p => p.foot === 'Left').length;
  const rightFooted = total - leftFooted;

  // Initialize group counts
  const groups = {
    Forwards: 0,
    Midfielders: 0,
    Defenders: 0,
    Goalkeepers: 0
  };

  // Count players by group based on their position
  players.forEach(player => {
    for (const [group, positions] of Object.entries(POSITION_GROUPS)) {
      if (positions.includes(player.position)) {
        groups[group]++;
        break;
      }
    }
  });

  return (
    <div className="w-full bg-gray-800 text-white p-4 mt-4 rounded-xl shadow-lg">
      <h2 className="text-xl font-bold mb-2">Team Summary</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        <div>Total Players: {total}</div>
        <div>Total Goals: {goals}</div>
        <div>Total Assists: {assists}</div>
        <div>Avg Fitness: {avgFitness}%</div>
        <div>Left-Footed: {leftFooted}</div>
        <div>Right-Footed: {rightFooted}</div>
        <div>Forwards: {groups.Forwards}</div>
        <div>Midfielders: {groups.Midfielders}</div>
        <div>Defenders: {groups.Defenders}</div>
        <div>Goalkeepers: {groups.Goalkeepers}</div>
      </div>
    </div>
  );
};

export default SummaryContainer;
